 p_id=>wwv_flow_api.id(67720713380281317230)
,p_process_sequence=>1
,p_process_point=>'ON_DEMAND'
,p_process_type=>'NATIVE_PLSQL'
,p_process_name=>'GET_LOV'
,p_process_sql_clob=>wwv_flow_utilities.join(wwv_flow_t_varchar2(
'DECLARE',
'  l_cursor  PLS_INTEGER;',
'  l_status  PLS_INTEGER;',
'  l_col_cnt PLS_INTEGER;',
'  l_row_cnt SIMPLE_INTEGER := 0;',
'  l_column  dbms_sql.desc_tab2;',
'  l_value   VARCHAR2(32767);',
'  l_sql     VARCHAR2(32767);',
'BEGIN',
' ',
'  /* Get LOV query */',
'  SELECT COALESCE(c.inline_list_of_values, l.list_of_values_query) as qry',
'  INTO l_sql',
'  FROM apex_application_page_rpt_cols c',
'  LEFT JOIN apex_application_lovs l',
'    ON c.named_list_of_values = l.list_of_values_name',
'    AND l.application_id = :APP_ID',
'  WHERE c.application_id = :APP_ID',
'  AND c.page_id = :APP_PAGE_ID',
'  AND c.column_alias = APEX_APPLICATION.G_x02',
'  ;',
'   ',
'  /* Get LOV query columns */',
'  l_cursor := dbms_sql.open_cursor;',
'  dbms_sql.parse (l_cursor, l_sql, dbms_sql.native);',
'  dbms_sql.describe_columns2(l_cursor, l_col_cnt, l_column);',
'  dbms_sql.close_cursor(l_cursor);',
'   ',
'  /* Check that query do have return and display value columns */',
'  IF l_column.count != 2 THEN',
'    raise_application_error (-20001, ''Invalid list of values query'');',
'  END IF;',
'   ',
'  /* Add autocomplete search to LOV query */',
'  l_sql := ''SELECT * FROM (''',
'    || l_sql',
'    || '') WHERE INSTR(UPPER(''',
'    || l_column(1).col_name',
'    || ''), :term) > 0''',
'    || '' ORDER BY 1''',
'    ;',
'   ',
'  l_cursor := dbms_sql.open_cursor;',
'  dbms_sql.parse (l_cursor, l_sql, dbms_sql.native);',
'  dbms_sql.bind_variable(l_cursor, '':term'', UPPER( APEX_APPLICATION.G_x01));',
'  dbms_sql.describe_columns2(l_cursor, l_col_cnt, l_column);',
'   ',
'  FOR i IN 1 .. l_column.count',
'  LOOP',
'    dbms_sql.define_column(l_cursor, i, l_value, 32767);',
'  END LOOP;',
'   ',
'  l_status := dbms_sql.execute(l_cursor);',
'   ',
'  /* Construct return data */',
'  LOOP',
'    EXIT',
'  WHEN (dbms_sql.fetch_rows(l_cursor) <= 0 );',
'  ',
'    l_row_cnt := l_row_cnt + 1;',
'    IF l_row_cnt = 1 THEN',
'      htp.prn(''{"row":[{'');',
'    ELSE',
'      htp.prn('',{'');',
'    END IF;',
'     ',
'    FOR i IN 1 .. l_column.count',
'    LOOP',
'      dbms_sql.column_value(l_cursor, i, l_value);',
'      IF i = 1 THEN',
'        htp.prn(''"value":"'' || l_value || ''"'');',
'      ELSIF i = 2 THEN',
'        htp.prn('',"retval":"'' || l_value || ''"}'');',
'      END IF;',
'    END LOOP;  ',
'  END LOOP;',
' ',
'  IF l_row_cnt = 0 THEN',
'    htp.prn(''{"row":[]}'');',
'  ELSE',
'    htp.prn('']}'');',
'  END IF;',
'   ',
'  dbms_sql.close_cursor(l_cursor);',
'   ',
'END;'))
,p_security_scheme=>'MUST_NOT_BE_PUBLIC_USER'
);
wwv_flow_api.create_flow_process(
 p_id=>wwv_flow_api.id(67704434570013613952)
,p_process_sequence=>1
,p_process_point=>'ON_NEW_INSTANCE'
,p_process_type=>'NATIVE_PLSQL'
,p_process_name=>'Create apex_page_items collection'
,p_process_sql_clob=>wwv_flow_utilities.join(wwv_flow_t_varchar2(
'DECLARE',
'',
'  lv_workspace VARCHAR2(60);',
'  ',
'BEGIN',
'',
'IF APEX_COLLECTION.COLLECTION_EXISTS (''ATAF_APEX_PAGE_ITEMS_V'') = false THEN',
'',
'select WORKSPACE INTO lv_workspace from APEX_APPLICATIONS where application_id = :app_id;',
'',
'APEX_UTIL.set_session_state(''ATAF_WORKSPACE'',lv_workspace);',
'',
'',
'',
'  apex_collection.create_collection_from_query( ',
'    p_collection_name => ''APEX_PAGE_ITEMS'',',
'    p_query => q''!select ',
'                   APPLICATION_ID,',
'                   PAGE_ID,',
'                   LABEL,',
'                   TYPE,',
'                   DOM_ID,',
'                   NAME,',
'                   DISPLAY_SEQUENCE,',
'                   ID,',
'                   ELEMENT_TYPE,',
'                   REGION_ID,',
'                   REGION_NAME,',
'                   DISPLAY_SEQUENCE1,',
'                   DISPLAY_SEQUENCE2',
'                 from ATAF_APEX_PAGE_ITEMS_V',
'                 where display = ''Y'' !'');',
'',
'END IF;',
'',
'END;'))
,p_process_error_message=>'There was a problem creating the APEX_PAGE_ITEMS collection.'
);
end;
