begin
wwv_flow_api.create_plugin(
 p_id=>wwv_flow_api.id(69277839291514130083)
,p_plugin_type=>'DYNAMIC ACTION'
,p_name=>'COM.ANKERPOORT.APEX.TOOLTIP'
,p_display_name=>'Tooltip'
,p_category=>'NOTIFICATION'
,p_supported_ui_types=>'DESKTOP'
,p_image_prefix => nvl(wwv_flow_application_install.get_static_plugin_file_prefix('DYNAMIC ACTION','COM.ANKERPOORT.APEX.TOOLTIP'),'')
,p_plsql_code=>wwv_flow_utilities.join(wwv_flow_t_varchar2(
'function render_tooltip(p_dynamic_action  in apex_plugin.t_dynamic_action,',
'                        p_plugin          in apex_plugin.t_plugin)',
'  return apex_plugin.t_dynamic_action_render_result',
'is',
'  l_result  apex_plugin.t_dynamic_action_render_result;',
'begin',
'  --',
'  -- During plug-in development it''s very helpful to have some debug information',
'  --',
'  if apex_application.g_debug',
'  then',
'    apex_plugin_util.debug_dynamic_action(p_plugin => p_plugin, p_dynamic_action => p_dynamic_action);',
'  end if;',
'',
'  --',
'  -- Include the stylesheet the plug-in uses.',
'  --',
'  apex_css.',
'   add_file(p_name => ''jquery.ui.tooltip'', p_directory => p_plugin.file_prefix, p_version => null);',
'  --',
'  -- Register the javascript library the plug-in uses.',
'  --',
'  apex_javascript.',
'   add_library(p_name => ''jquery.metadata'', p_directory => p_plugin.file_prefix, p_version => null);',
'  apex_javascript.',
'   add_library(p_name => ''jquery.hoverIntent.min'', p_directory => p_plugin.file_prefix, p_version => null);',
'  apex_javascript.',
'   add_library(p_name => ''jquery.ui.tooltip.min'', p_directory => p_plugin.file_prefix, p_version => null);',
'  apex_javascript.',
'   add_library(p_name       => ''com_ankerpoort_apex_tooltip.min'',',
'               p_directory  => p_plugin.file_prefix,',
'               p_version    => null);',
'',
'  --',
'  -- Register the javascript callout and attribute values which should be transfered to the browser.',
'  --',
'  l_result.javascript_function := ''com_ankerpoort_apex_tooltip'';',
'  l_result.ajax_identifier := apex_plugin.get_ajax_identifier;',
'  -- Content Type',
'  l_result.attribute_01 := coalesce(p_dynamic_action.attribute_01, p_plugin.attribute_01);',
'  -- Page Items To Submit',
'  l_result.attribute_02 := coalesce(p_dynamic_action.attribute_03, p_plugin.attribute_03);',
'  -- Position',
'  l_result.attribute_03 := coalesce(p_dynamic_action.attribute_04, p_plugin.attribute_04, ''right'');',
'  -- Width',
'  l_result.attribute_04 := coalesce(p_dynamic_action.attribute_05, p_plugin.attribute_05, ''auto'');',
'  -- Offset',
'  l_result.attribute_05 := coalesce(p_dynamic_action.attribute_06, p_plugin.attribute_06, ''0'');',
'  -- Content Style',
'  l_result.attribute_06 :=',
'    coalesce(p_dynamic_action.attribute_07, p_plugin.attribute_07, ''ui-widget-content'');',
'  -- Show Connector',
'  l_result.attribute_07 :=',
'    coalesce(p_dynamic_action.attribute_08, p_plugin.attribute_08, ''unfilled'');',
'  -- Cache',
'  l_result.attribute_08 := coalesce(p_dynamic_action.attribute_09, p_plugin.attribute_09, ''Y'');',
'  -- Height',
'  l_result.attribute_09 := coalesce(p_dynamic_action.attribute_10, p_plugin.attribute_10, ''auto'');',
'',
'  return l_result;',
'end render_tooltip;',
'',
'function ajax_tooltip(p_dynamic_action  in apex_plugin.t_dynamic_action,',
'                      p_plugin          in apex_plugin.t_plugin)',
'  return apex_plugin.t_dynamic_action_ajax_result',
'is',
'  l_return  apex_plugin.t_dynamic_action_ajax_result;',
'begin',
'  --',
'  -- Executes a PL/SQL code block.',
'  --',
'  apex_plugin_util.',
'   execute_plsql_code(coalesce(p_dynamic_action.attribute_02, p_plugin.attribute_02));',
'  return l_return;',
'end ajax_tooltip;'))
