,p_plug_source=>wwv_flow_utilities.join(wwv_flow_t_varchar2(
'<div id="dialog"></div>',
'<script type="text/javascript">',
'',
' var p_changes = false;',
'',
' $(document).ready(function() {',
'     $(''.apexir_WORKSHEET_DATA,[id^="datatable"]'').live(''change'',function(){',
'       p_changes = true;',
'     });',
'     $(''a[href^="javascript:gReport"],a[href^="javascript:apex.widget.report.paginate"]'').live(''click'',',
'     function(event) {',
'         if (p_changes) {',
'             if (confirm(''"SAVE_CHANGES_MSG"'')) {',
'             p_changes = false;',
'             }',
'             else {',
'                 event.preventDefault();',
'             }',
'         }',
'     });',
' });',
'',
'// will select all contents of a text field on focus ',
'',
' $(document).ready(function () {',
'    $("input[type=text]").focus(function () {',
'      // Select field contents',
'      this.select();',
'    });',
'  });',
'',
'function moveUpDown(pThis, pEvent){',
'   var keynum;',
'   var current = document.getElementsByName( pThis.name );',
'',
'   if(window.event) // IE',
'     { keynum = pEvent.keyCode; }',
'   else if(pEvent.which) // Netscape/Firefox/Opera',
'     { keynum = pEvent.which; }',
'',
'   if (keynum == 40 || keynum == 38) // Key-Up or Key-Down',
'     { for (i=0;i < current.length;i++)',
'         { if ( current[i].id == pThis.id ) // This is current row',
'          { if (keynum == 40) // Move down',
'          { current[Math.min(current.length - 1,i+1)].focus(); }',
'           else // Move up',
'           { current[Math.max(0,i-1)].focus(); }',
'       }',
'     }',
'   }',
'}',
'',
'var htmldb_delete_message=''"DELETE_CONFIRM_MSG"'';',
'',
'',
'// Add row to tabular form when return clicked',
'$(document).keypress(function(e) {',
'  if(e.which == 13) {',
'    $( "#addRowButton" ).click();',
'  }',
'});',
'',
'function prepareDialog(pThis, pWidth, pHeight, pTitle, pModal) {',
' ',
'    //MUST BE CALLED FROM WITHIN THE "a" TAG!',
'    //Get the href value (inlcuding any checksum)',
' ',
'    var vHref = $(pThis).attr("href");',
'    //For buttons',
'    // Strips out link from existing javascript function',
'    vHref = vHref.replace(''javascript:apex.navigation.redirect(\'''', '''');',
'    vHref = vHref.replace(''javascript:apex.navigation.popup.url(\'''', '''');',
'    vHref = vHref.replace(''\'');'', '''');',
' ',
'    //remove href and onClick attributes to prevent default action',
'    $(pThis).removeAttr("href");',
'    $(pThis).removeAttr("onClick");',
'    $(pThis).removeAttr("onclick");',
'    $(pThis).removeAttr("onmousedown");',
'    $(pThis).prop("onclick", null);',
'    $(pThis).prop("href", null);',
'    $(pThis).prop("onmousedown", null);',
' ',
'    //Add a new onClick event and a new href (you need the href for IE) using the href with checksums etc',
'    $(pThis).attr("href", ''javascript:showDialog(\'''' + vHref + ''\'', '' + pWidth + '', '' + pHeight + '', \'''' + pTitle + ''\'', \'''' + pModal + ''\'');'');',
'    $(pThis).click(function() {',
'        javascript: showDialog(vHref, pWidth, pHeight, pTitle, pModal);',
'    });',
' ',
'    //NOTE: As the trigger point is the onClick event defined in the tag itself preventDefault() will not work on the first click!',
'    // tried .live( "click", function(e){e.preventDefault()})',
'    // tried .on( "click", function(e){e.preventDefault()})',
'    // tried .click( function(e){e.preventDefault()})',
'    // All returning false',
'    // ALL have problems firing on first click',
'    //Show the dialog',
' ',
'    showDialog(vHref, pWidth, pHeight, pTitle, pModal); ',
'    //This will seem odd.',
'    //Remember we just removed the onClick event above and replaced it so this will only run on the first click',
'}',
'',
'function showDialog(v_url, v_width, v_height, v_title, v_modal, v_x_close) {',
'    var v_iframe;',
'    if (v_height == null) {',
'        v_height = 400;',
'    }',
'    if (v_width == null) {',
'        v_width = 600;',
'    }',
'    if (v_title == null) {',
'        v_title = '''';',
'    }',
'    if (v_modal == null) {',
'        v_modal = true;',
'    }',
'    v_iframe = ''<iframe width="100%" height="100%" frameborder="0" scrolling="auto" marginheight="0" marginwidth="0" src="'' + v_url + ''"></iframe>'';',
'    //$("#dialog").dialog(''destroy'');',
'    //inject iframe into div',
'    $("#dialog").html(v_iframe);',
'    $(''#dialog'').dialog({',
'        autoOpen: false,',
'        modal: v_modal,',
'        height: v_height + 100,',
'        width: v_width,',
'        position: ''center''',
'            // A Roxburgh 01/04/14 Fix for XSS vulnerability applied',
'            //, title: v_title',
'            ,',
'        title: $(''<div/>'').text(v_title).html().replace(''"'', ''&quot;''),',
'        hide: "fadeOut",',
'        show: "fadeIn",',
'        close: function(event, ui) {',
'            if (event.which == 27) {',
'                eval(v_x_close)',
'            }',
'        } /*Runs function defined by v_x_close when hitting escape*/',
'    });',
'    $(''#dialog'').dialog(''open'');',
'    /*Runs function defined by v_x_close when clicking X to close*/',
'    $(''.ui-icon-closethick'').bind(''click'', function() {',
'        eval(v_x_close);',
'    });',
'}',
'',
'function closeDialog(pRefresh)',
'{',
'    $(''#dialog'').dialog(''close'');',
'    if (pRefresh) {',
'      $(''#refresh'').click();',
'    }',
'    return false;',
'}',
'',
'</script>'))
